INSERT INTO public.games (game_id, name, price, poster, description, platforms, youtube_link, mc_score, user_score, overall_score, developer, publisher, release_date, setting, main_genre, side_genre1, side_genre2, is_indie, processor, graphics_card, ram, free_memory) VALUES (34, 'Grand Theft Auto V: Premium Online Edition', 749.00, 'gameposter_30_1.jpg', 'GTA 5 — это самый масштабный и многообещающий проект Rockstar Games. Игра перенесет вас в солнечный Лос-Сантос — город, где жизнь кипит на каждом шагу, откуда никогда не захочется уезжать и куда жаждет попасть каждый. Grand Theft Auto 5 стала новым витком в эволюции экшен игр с открытым миром от третьего лица, предложив игрокам множество развлечений, огромнейший детализированный мир, бессчетное количество харизматичных персонажей и необыкновенный сатирический взгляд на американскую мечту. Но какая же GTA без кучи денег, полного гараж машин, склада оружия и целого криминального бизнеса, а в GTA Online их порой достать ой как непросто.

И здесь на помощь приходит Criminal Enterprise Starter Pack, дополнение для мультиплеерного режима Grand Theft Auto V. Дополнение дарит игроку один миллион долларов, офис в престижном здании Maze Bank, бункер с Палето для сбыта оружия (неплохой источник доходов), мастерскую в пустыне Сенора (очень удобно там печатать фальшивые деньги), клуб для прикрытия бизнеса и проведения безбашенных вечеринок, большую квартиру со стильным ремонтом и гараж на целых десять автомобилей. Кроме того, в вашем распоряжении окажется боевой вертолет для самых напряженных разборок. А чтобы еще больше повысить свой авторитет и шансы на выживание, совершенно бесплатно заберите в AmmuNation своё оружие: гранатомет и два вида винтовок.

Основная игра и DLC для Online-режима с огромным количеством контента – и все в одном наборе. Чего еще можно желать? Оружие в руки, стильный костюм на туловище, мощный спорткар под пятую точку – и вперёд, выполнять опасные задания, участвовать в разборках между бандами, выносить из банковских хранилищ миллионы долларов. А как поднаберетесь опыта и авторитета среди местных воротил, сможете организовать своё «совершенно легальное» дело – там, глядишь, и дорастете до криминальной главы Лос-Сантоса. Преступная империя – дело непростое: руководить ей сложно, опасно для жизни, зато какие выгоды!', 'PS4|PS5|PC', 'https://www.youtube.com/embed/VNbONMSObfs', 80.00000000, null, 80.00000000, 'Rockstar North', 'Rockstar Games', '2015-04-14', 'Ограбления', 'Экшен', 'Приключения', 'Гонки', false, 'Intel Core 2 Q6600 @ 2.40 ГГц / AMD Phenom 9850 @ 2.5 ГГц', 'NVIDIA 9800 GT, 1 ГБ / AMD HD 4870, 1 ГБ', 4, 72);
